#!/bin/bash

# Czech Vocabulary Image Downloader
# Downloads images for 300 Czech words organized in 12 categories

# Create directory structure
mkdir -p czech_flashcards/{rodina,jidlo,zvirata,barvy,cisla,telo,obleceni,dum,doprava,pocasi,prace,cas}

# Function to download image with retry
download_image() {
    local url="$1"
    local filepath="$2"
    local max_retries=3
    local retry=0
    
    while [ $retry -lt $max_retries ]; do
        if curl -s -L "$url" -o "$filepath" && [ -s "$filepath" ]; then
            echo "✓ Downloaded: $filepath"
            return 0
        else
            retry=$((retry+1))
            echo "⟳ Retry $retry/$max_retries: $filepath"
            sleep 1
        fi
    done
    
    echo "✗ Failed: $filepath"
    return 1
}

